/**
 * https://core.telegram.org/bots/api#transactionpartner  
 * This object describes the source of a transaction, or its recipient for outgoing transactions. Currently, it can be one of
 * - TransactionPartnerUser
 * - TransactionPartnerChat
 * - TransactionPartnerAffiliateProgram
 * - TransactionPartnerFragment
 * - TransactionPartnerTelegramAds
 * - TransactionPartnerTelegramApi
 * - TransactionPartnerOther
*/
export type TransactionPartner = TransactionPartnerUser | TransactionPartnerChat | TransactionPartnerAffiliateProgram | TransactionPartnerFragment | TransactionPartnerTelegramAds | TransactionPartnerTelegramApi | TransactionPartnerOther;

/**
 * https://core.telegram.org/bots/api#transactionpartneruser  
 * Describes a transaction with a user.
*/
export interface TransactionPartnerUser {
    /** String | Type of the transaction partner, always "user" */
    type: 'user';
    /** String | Type of the transaction, currently one of "invoice_payment" for payments via invoices, "paid_media_payment" for payments for paid media, "gift_purchase" for gifts sent by the bot, "premium_purchase" for Telegram Premium subscriptions gifted by the bot, "business_account_transfer" for direct transfers from managed business accounts */
    transaction_type: string;
    /** User | Information about the user */
    user: User;
    /** AffiliateInfo | Optional. Information about the affiliate that received a commission via this transaction. Can be available only for "invoice_payment" and "paid_media_payment" transactions. */
    affiliate?: AffiliateInfo;
    /** String | Optional. Bot-specified invoice payload. Can be available only for "invoice_payment" transactions. */
    invoice_payload?: string;
    /** Integer | Optional. The duration of the paid subscription. Can be available only for "invoice_payment" transactions. */
    subscription_period?: number;
    /** Array of PaidMedia | Optional. Information about the paid media bought by the user; for "paid_media_payment" transactions only */
    paid_media?: Array<PaidMedia>;
    /** String | Optional. Bot-specified paid media payload. Can be available only for "paid_media_payment" transactions. */
    paid_media_payload?: string;
    /** Gift | Optional. The gift sent to the user by the bot; for "gift_purchase" transactions only */
    gift?: Gift;
    /** Integer | Optional. Number of months the gifted Telegram Premium subscription will be active for; for "premium_purchase" transactions only */
    premium_subscription_duration?: number;
}

/**
 * https://core.telegram.org/bots/api#transactionpartnerchat  
 * Describes a transaction with a chat.
*/
export interface TransactionPartnerChat {
    /** String | Type of the transaction partner, always "chat" */
    type: 'chat';
    /** Chat | Information about the chat */
    chat: Chat;
    /** Gift | Optional. The gift sent to the chat by the bot */
    gift?: Gift;
}

/**
 * https://core.telegram.org/bots/api#transactionpartneraffiliateprogram  
 * Describes the affiliate program that issued the affiliate commission received via this transaction.
*/
